import React, { Component } from "react";
import NewsItem from "./NewsItem";

export class News extends Component {

  constructor() {
    super();
    this.state = {
      articles: [],
      loading: false,
      page:1
    };
  }
  async componentDidMount(){
    let url = "https://newsapi.org/v2/top-headlines?country=in&apiKey=93c6dbbc180a49ccac6aa1cc0cd8336e&page=1&pageSize=20"
    let data = await fetch(url);
    let parsedData = await data.json();
    console.log(parsedData)
    this.setState({articles: parsedData.articles,totalResults: parsedData.totalResults})
  }
   handPrevClick = async() =>{
      console.log("previous")
      let url = `https://newsapi.org/v2/top-headlines?country=in&apiKey=93c6dbbc180a49ccac6aa1cc0cd8336e&page=${this.state.page-1}&pageSize=20`;
      let data = await fetch(url);
      let parsedData = await data.json();
      console.log(parsedData)
      this.setState({
        page:this.state.page-1,
        articles: parsedData.articles
    })

  }
  handleNextClick = async () =>{
    console.log("next")
    if(this.state.pagep  + 1 > Math.ceil(this.state.totalResults/20)){

    }
    else{
      let url = `https://newsapi.org/v2/top-headlines?country=in&apiKey=93c6dbbc180a49ccac6aa1cc0cd8336e&page=${this.state.page+1}&pageSize=20`
      let data = await fetch(url);
      let parsedData = await data.json();
      console.log(parsedData)
      this.setState({
        page:this.state.page+1,
        articles: parsedData.articles
      })
    }

  }

  render() {
    return (
      <div className="container my-3">
        <h1 className="text-center">News Monkey Top headlines</h1>
        <h1>News Monkey Top headlines</h1>

        <div className="row">
          {this.state.articles.map((element) => {
            return (
              <div className="col-md-4" key={element.url}>
                <NewsItem
                title={element.title?element.title:""}
                  description={element.description?element.description:""}
                  imageUrl={element.urlToImage?element.urlToImage:""}
                  newsUrl={element.url}
                />
              </div>
            );
          })}
          <div className="container d-flex justify-content-between">
          <button disabled={this.state.page<=1} type="button" className="btn btn-dark" onClick={this.handPrevClick}> &larr; Previous</button>
          <button type="button" className="btn btn-dark" onClick={this.handleNextClick}>Next&rarr;</button>
          </div>
        </div>
      </div>
    );
  }
}

export default News;
