import React, { Component } from "react";
import NewsItem from "./NewsItem";
import Spinner from "./Spinner";
import PropTypes from 'prop-types'
import InfiniteScroll from "react-infinite-scroll-component";
export class News extends Component {

  static defaultProps = {
    country: "in",
    pageSize: 8,
    category: "general"
  }
  static propTypes = {
    country: PropTypes.string,
    pageSize: PropTypes.number,
    category: PropTypes.string
  }
  capitalizeFirstLetter = (string) => {
    return string.charAt(0).toUpperCase() + string.slice(1);
  }
  constructor(props) {
    super(props);
    this.state = {
      articles: [],
      loading: false,
      page: 1,
      totalResults:0

    };
    document.title = `${this.capitalizeFirstLetter(this.props.category)} - NewsMonkey`;
  }
  async updateNews() {
    const url = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apiKey=93c6dbbc180a49ccac6aa1cc0cd8336e&page=${this.state.page}&pageSize=${this.props.pageSize}`
    this.setState({
      loading: true
    })
    let data = await fetch(url);
    let parsedData = await data.json();
    console.log(parsedData)
    this.setState({ articles: parsedData.articles, totalResults: parsedData.totalResults, loading: false })
  }
  async componentDidMount() {
    // let url = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apiKey=93c6dbbc180a49ccac6aa1cc0cd8336e&page=1&pageSize=${this.props.pageSize}`
    // this.setState({
    //   loading:true
    // })
    // let data = await fetch(url);
    // let parsedData = await data.json();
    // console.log(parsedData)
    // this.setState({articles: parsedData.articles,totalResults: parsedData.totalResults,loading:false})
    this.updateNews();
  }
  handPrevClick = async () => {
    console.log("previous")
    //   let url = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apiKey=93c6dbbc180a49ccac6aa1cc0cd8336e&page=${this.state.page-1}&pageSize=${this.props.pageSize}`;
    //   this.setState({
    //     loading:true
    //   })
    //   let data = await fetch(url);
    //   let parsedData = await data.json();
    //   this.setState({loading:false})
    //   this.setState({
    //     page:this.state.page-1,
    //     articles: parsedData.articles
    // })
    this.setState({
      page: this.state.page - 1
    })
    this.updateNews()

  }
  handleNextClick = async () => {
    console.log("next")
    // if(!(this.state.page  + 1 > Math.ceil(this.state.totalResults/this.props.pageSize))){
    //   let url = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apiKey=93c6dbbc180a49ccac6aa1cc0cd8336e&page=${this.state.page+1}&pageSize=${this.props.pageSize}`
    //   this.setState({
    //     loading:true
    //   })
    //   let data = await fetch(url);
    //   let parsedData = await data.json();
    //   this.setState({loading:false})
    //     this.setState({
    //     page:this.state.page+1,
    //     articles: parsedData.articles
    //   })
    // }
    this.setState({
      page: this.state.page + 1
    })
    this.updateNews()

  }

  render() {
    return (
      <div className="container my-3">
        <h1 className="text-center" style={{ margin: "40px" }}>Top {this.capitalizeFirstLetter(this.props.category)} Headlines</h1>
        {/* {this.state.loading && <Spinner/>}
         */}
                <InfiniteScroll
                    dataLength={this.state.articles.length}
                    next={this.fetchMoreData}
                    hasMore={this.state.articles.length !== this.state.totalResults}
                    loader={<Spinner/>}
                > 
                    <div className="container">

                    <div className="row">
                        {this.state.articles.map((element) => {
                            return <div className="col-md-4" key={element.url}>
                                <NewsItem title={element.title ? element.title : ""} description={element.description ? element.description : ""} imageUrl={element.urlToImage} newsUrl={element.url} author={element.author} date={element.publishedAt} source={element.source.name} />
                            </div>
                        })}
                    </div>
                    </div> 
                </InfiniteScroll>
        <div className="container d-flex justify-content-between">
          <button disabled={this.state.page <= 1} type="button" className="btn btn-dark" onClick={this.handPrevClick}> &larr; Previous</button>
          <button disabled={this.state.page + 1 > Math.ceil(this.state.totalResults / this.props.pageSize)} type="button" className="btn btn-dark" onClick={this.handleNextClick}>Next&rarr;</button>
        </div>
      </div>
      // </div >
    );
  }
}

export default News;
