import React, { Component } from "react";
import NewsItem from "./NewsItem";
import Spinner from "./Spinner";
import PropTypes from 'prop-types'
export class News extends Component {

  static defaultProps = {
    country:"in",
    pageSize:8,
    categories:PropTypes.string
  }
  static PropTypes = {
    country:PropTypes.string,
    pageSize: PropTypes.number,
    categories: PropTypes.string
  }
  constructor() {
    super();
    this.state = {
      articles: [],
      loading: false,
      page:1
    };
  }
  async componentDidMount(){
    let url = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&apiKey=93c6dbbc180a49ccac6aa1cc0cd8336e&page=1&pageSize=${this.props.pageSize}`
    this.setState({
      loading:true
    })
    let data = await fetch(url);
    let parsedData = await data.json();
    console.log(parsedData)
    this.setState({articles: parsedData.articles,totalResults: parsedData.totalResults,loading:false})
  }
   handPrevClick = async() =>{
      console.log("previous")
      let url = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&apiKey=93c6dbbc180a49ccac6aa1cc0cd8336e&page=${this.state.page-1}&pageSize=${this.props.pageSize}`;
      this.setState({
        loading:true
      })
      let data = await fetch(url);
      let parsedData = await data.json();
      this.setState({loading:false})
      this.setState({
        page:this.state.page-1,
        articles: parsedData.articles
    })

  }
  handleNextClick = async () =>{
    console.log("next")
    if(!(this.state.page  + 1 > Math.ceil(this.state.totalResults/this.props.pageSize))){
      let url = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&apiKey=93c6dbbc180a49ccac6aa1cc0cd8336e&page=${this.state.page+1}&pageSize=${this.props.pageSize}`
      this.setState({
        loading:true
      })
      let data = await fetch(url);
      let parsedData = await data.json();
      this.setState({loading:false})
        this.setState({
        page:this.state.page+1,
        articles: parsedData.articles
      })
    }

  }

  render() {
    return (
      <div className="container my-3">
        <h1 className="text-center">News Monkey Top headlines</h1>
        {this.state.loading && <Spinner/>}
        <div className="row">
          {!this.state.loading && this.state.articles.map((element) => {
            return (
              <div className="col-md-4" key={element.url}>
                <NewsItem
                title={element.title?element.title:""}
                  description={element.description?element.description:""}
                  imageUrl={element.urlToImage?element.urlToImage:""}
                  newsUrl={element.url}
                />
              </div>
            );
          })}
          <div className="container d-flex justify-content-between">
          <button disabled={this.state.page<=1} type="button" className="btn btn-dark" onClick={this.handPrevClick}> &larr; Previous</button>
          <button disabled={this.state.page  + 1 > Math.ceil(this.state.totalResults/this.props.pageSize)} type="button" className="btn btn-dark" onClick={this.handleNextClick}>Next&rarr;</button>
          </div>
        </div>
      </div>
    );
  }
}

export default News;
