// import logo from './logo.svg';
import './App.css';

import News from './components/News';
import React, { Component } from 'react'
import LoadingBar from "react-top-loading-bar";

import Navbar from './components/Navbar';
import {
  BrowserRouter as Router,
  Routes,
  Route
} from "react-router-dom";
// import NewsItem from './components/NewsItem';

export default class App extends Component {
  // c='Parshva';
  pageSize = 15
  state = {
    progress:0
  }
  setProgress(progress){
    this.setState({
      progress:progress
    })
  }
  render() {
    return (

      <div>
      <Router>
        <Navbar />
        <LoadingBar
        height={3}
        color='#f11946'
        progress={this.state.progress} 
      />
        <Routes>
          <Route path='/' element={<News key='general'  setProgress={this.setProgress} pageSize={this.pageSize} country='in' category='general' />}></Route>
          <Route path='/business' element={<News key='business'  setProgress={this.setProgress} pageSize={this.pageSize} country='in' category='business' />}></Route>
          <Route path='/entertainment' element={<News key='entertainment'  setProgress={this.setProgress} pageSize={this.pageSize} country='in' category='entertainment' />}></Route>
          <Route path='/health' element={<News key='health'  setProgress={this.setProgress} pageSize={this.pageSize} country='in' category='health' />}></Route>
          <Route path='/science' element={<News key='science'  setProgress={this.setProgress} pageSize={this.pageSize} country='in' category='science' />}></Route>
          <Route path='/sports' element={<News key='sports'  setProgress={this.setProgress} pageSize={this.pageSize} country='in' category='sports' />}></Route>
          <Route path='/technology' element={<News key='technology'  setProgress={this.setProgress} pageSize={this.pageSize} country='in' category='technology' />}></Route>
        </Routes>
      </Router>
    </div>

    )
  }
}
