// import logo from './logo.svg';
import "./App.css";

import News from "./components/News";
import React, { Component } from "react";
import LoadingBar from "react-top-loading-bar";

import Navbar from "./components/Navbar";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
// import NewsItem from './components/NewsItem';

export default class App extends Component {
  // c='Parshva';
  pageSize = 15;
  state = {
    progress: 0,
  };
  setProgress = (progress) => {
    this.setState({
      progress: progress,
    });
  };
  render() {
    return (
      <div>
        <Router>
          <Navbar />
          <LoadingBar
            height={4}
            color="#f11946"
            progress={this.state.progress}
          />
          <Routes>
            <Route
              path="/"
              element={
                <News
                  key="general"
                  setProgress={this.setProgress}
                  pageSize={this.pageSize}
                  country="in"
                  category="general"
                  apiKey={this.apiKey} 
                />
              }
            ></Route>
            <Route
              path="/business"
              element={
                <News
                  key="business"
                  setProgress={this.setProgress}
                  pageSize={this.pageSize}
                  country="in"
                  category="business"
                  apiKey={this.apiKey} 
                />
              }
            ></Route>
            <Route
              path="/entertainment"
              element={
                <News
                  key="entertainment"
                  setProgress={this.setProgress}
                  pageSize={this.pageSize}
                  country="in"
                  category="entertainment"
                  apiKey={this.apiKey} 
                />
              }
            ></Route>
            <Route
              path="/health"
              element={
                <News
                  key="health"
                  setProgress={this.setProgress}
                  pageSize={this.pageSize}
                  country="in"
                  category="health"
                  apiKey={this.apiKey} 
                />
              }
            ></Route>
            <Route
              path="/science"
              element={
                <News
                  key="science"
                  setProgress={this.setProgress}
                  pageSize={this.pageSize}
                  country="in"
                  category="science"
                  apiKey={this.apiKey} 
                />
              }
            ></Route>
            <Route
              path="/sports"
              element={
                <News
                  key="sports"
                  setProgress={this.setProgress}
                  pageSize={this.pageSize}
                  country="in"
                  category="sports"
                  apiKey={this.apiKey} 
                />
              }
            ></Route>
            <Route
              path="/technology"
              element={
                <News
                  key="technology"
                  setProgress={this.setProgress}
                  pageSize={this.pageSize}
                  country="in"
                  category="technology"
                  apiKey={this.apiKey} 
                />
              }
            ></Route>
          </Routes>
        </Router>
      </div>
    );
  }
}
